mapper.select:
select

from tc_classes t1 
where  t1.id = #{id}

mapper.update:
update tc_classes set version=version+1
,last_updated=now()
,update_id=#{update_id}
,update_ip=#{update_ip}
,name=#{name}
,sort_num=#{sort_num}
,keyword=#{keyword}
,brief_info=#{brief_info}
,detail_info=#{detail_info}
,note=#{note}
,del_flag=#{del_flag}
,pic_url=#{pic_url}
where  id = #{id}

update tc_classes set version=version+1
,last_updated=now()
,update_id=#{update_id}
,update_ip=#{update_ip}
,del_flag='1'
where  id = #{id}

update tc_classes set version=version+1
,last_updated=now()
,update_id=#{update_id}
,update_ip=#{update_ip}
,del_flag='0'
where  id = #{id}
