mapper.select:
select

from tc_classes_vs_student t1 
where  t1.student_id = #{student_id} and  t1.classes_id = #{classes_id}

mapper.delete:
delete
from tc_classes_vs_student
where  student_id = #{student_id} and  classes_id = #{classes_id}

mapper.update:
update tc_classes_vs_student set version=version+1
,last_updated=now()
,update_id=#{update_id}
,update_ip=#{update_ip}
where  student_id = #{student_id} and  classes_id = #{classes_id}

update tc_classes_vs_student set version=version+1
,last_updated=now()
,update_id=#{update_id}
,update_ip=#{update_ip}
,del_flag='1'
where  student_id = #{student_id} and  classes_id = #{classes_id}

update tc_classes_vs_student set version=version+1
,last_updated=now()
,update_id=#{update_id}
,update_ip=#{update_ip}
,del_flag='0'
where  student_id = #{student_id} and  classes_id = #{classes_id}
