mapper.select:
select 

from tc_comment t1
where t1.id = #{id,jdbcType=VARCHAR}

select
,t2.name as user_name from tc_comment t1 left join tc_member t2 on (t1.create_id=t2.user_id) where t1.info_id=#{info_id}
and t1.del_flag=#{del_flag}
and t1.del_flag='0'
order by t1.date_created desc

    	 select 
			
		 from tc_comment t1
		 where t1.info_id=#{info_id}
		 
	    	
	    		and t1.del_flag=#{del_flag}
	    	
	    	
	    	    and t1.del_flag='0'
	    	
	    
		 order by t1.date_created desc
