mapper.select:
select 

from tc_course_syllabus t1
where t1.id = #{id}

mapper.update:
    update tc_course_syllabus set version=version+1
    ,last_updated=now()
	,update_id=#{update_id}
	,update_ip=#{update_ip}
	,course_id=#{course_id}
	,teacher_id=#{teacher_id}
	,day=#{day}
	,begin_time=#{begin_time}
	,end_time=#{end_time}
	,note=#{note}
	,del_flag=#{del_flag}
	,course_status=#{course_status}
	,detail_info=#{detail_info}
	where id=#{id}
