mapper.update:
    update tc_course_syllabus_items set version=version+1
    ,last_updated=now()
	,update_id=#{update_id}
	,update_ip=#{update_ip}
	,student_id=#{student_id}
	,course_id=#{course_id}
	,teacher_id=#{teacher_id}
	,teacher_score=#{teacher_score}
	,teacher_score_note=#{teacher_score_note}
	,student_status=#{student_status}
	,student_status_note=#{student_status_note}
	,note=#{note}
	,del_flag=#{del_flag}
	where id=#{id}

mapper.select:
select t2.id, t2.name, t2.age, t2.sex, t2.hobby, t2.pic_url, t2.birthdate from tc_course_syllabus_items t1 left join tc_student t2 on (t2.id=t1.student_id) where t1.course_id=#{course_id}
and t2.del_flag=#{del_flag}
and t2.del_flag='0'
limit #{limit_s},#{limit_e}
