mapper.update:
    update tc_course_syllabus_items set version=version+1
    ,last_updated=now()
	,update_id=#{update_id}
	,update_ip=#{update_ip}
	,student_id=#{student_id}
	,course_syllabus_id=#{course_syllabus_id}
	,teacher_id=#{teacher_id}
	,teacher_score=#{teacher_score}
	,teacher_score_note=#{teacher_score_note}
	,student_status=#{student_status}
	,student_status_note=#{student_status_note}
	,note=#{note}
	,del_flag=#{del_flag}
	where id=#{id}

mapper.select:
select t2.id, t2.name, t2.age, t2.sex, t2.hobby, t2.pic_url, t2.birthdate from tc_course_syllabus_items t1 left join tc_student t2 on (t2.id=t1.student_id) where t1.course_syllabus_id=#{course_syllabus_id}
and t2.del_flag=#{del_flag}
and t2.del_flag='0'
limit #{limit_s},#{limit_e}

select t2.id, t2.course_id, t2.teacher_id, t2.day, t2.begin_time, t2.end_time, t2.note, t2.date_created, t2.create_id, t2.create_ip, t2.last_updated, t2.update_id, t2.update_ip, t2.del_flag, t2.version, t2.course_status, t2.detail_info, t2.addres, t3.title, t4.nickname as teacher_name from tc_course_syllabus_items t1 left join tc_course_syllabus t2 on (t2.id=t1.course_syllabus_id) left join tc_course t3 on (t3.id=t2.course_id) left join tc_member t4 on (t4.id=t2.teacher_id) where t1.student_id=#{student_id}
and t3.del_flag=#{del_flag}
and t3.del_flag='0'
