mapper.select:
select 

from tc_course_web_enroll t1
where t1.id = #{id}

mapper.update:
    update tc_course_web_enroll set version=version+1
    ,last_updated=now()
	,update_id=#{update_id}
	,update_ip=#{update_ip}
	,name=#{name}
	,sex=#{sex}
	,tel=#{tel}
	,course=#{course}
	,note=#{note}
	,del_flag=#{del_flag}
	,status=#{status}
	where id=#{id}
