mapper.update:
    update tc_student set version=version+1
    ,last_updated=now()
	,update_id=#{update_id}
	,update_ip=#{update_ip}
	,name=#{name}
	,age=#{age}
	,sex=#{sex}
	,hobby=#{hobby}
	,detail_info=#{detail_info}
	,note=#{note}
	,del_flag=#{del_flag}
	,pic_url=#{pic_url}
	,birthdate=#{birthdate}
	
	where id=#{id}

mapper.select:
select
from tc_student t1 where 1=1
and t1.del_flag=#{del_flag}
and t1.del_flag='0'
order by t1.name asc
limit #{limit_s},#{limit_e}
