mapper.update:
    update tc_sys_news set version=version+1
    ,last_updated=now()
	,update_id=#{update_id}
	,update_ip=#{update_ip}
	,sort_num=${sort_num}
	,title=#{title}
	,second_title=#{second_title}
	,keyword=#{keyword}
	,brief_info=#{brief_info}
	,detail_info=#{detail_info}
	,is_ontop=#{is_ontop}
	,click_count=${click_count}
	,msgtype=#{msgtype}
	,note=#{note}
	,del_flag=#{del_flag}
	,info_source=#{info_source}
	where id=#{id}
