mapper.update:
    update tc_sys_news set version=version+1
    ,last_updated=now()
	,update_id=#{update_id}
	,update_ip=#{update_ip}
	,sort_num=${sort_num}
	,title=#{title}
	,second_title=#{second_title}
	,keyword=#{keyword}
	,brief_info=#{brief_info}
	,detail_info=#{detail_info}
	,is_ontop=#{is_ontop}
	,click_count=${click_count}
	,msgtype=#{msgtype}
	,note=#{note}
	,del_flag=#{del_flag}
	,pic_url=#{pic_url}
	where id=#{id}

mapper.select:
    select
	
	from tc_sys_news t1
	left join tc_sys_news_vs_type t2 on (t1.id=t2.new_id)
    where 1=1
    
    	
    		and t1.del_flag=#{del_flag}
    	
    	
    	    and t1.del_flag='0'
    	
    
    and t1.info_source=#{info_source}
     and t2.type_id=#{type_id}
    group  by t1.id
    order by t1.last_updated desc

	 select
	
	,t3.parent_id
	from tc_sys_news t1
	left join tc_sys_news_vs_type t2 on (t1.id=t2.new_id)
	left join tc_sys_news_type t3 on (t3.id=t2.type_id)
    where 1=1
    
    	
    		and t1.del_flag=#{del_flag}
    	
    	
    	    and t1.del_flag='0'
    	
    
    and t1.info_source=#{info_source}
     and t2.type_id=#{type_id}
    group  by t1.id
    order by t1.last_updated desc
    
     limit #{limit_s},#{limit_e}
