mapper.insert:
insert into tc_wx_info
id , keyword , msgtype , tousername , fromusername , createtime , content , mediaid , title , description , musicurl , hqmusicurl , thumbmediaid , format , location_x , location_y , scale , label , articlecount , articles_id , picurl , url , default_flag , subscribe_flag ,location_precision ,event ,eventkey ,ticket , date_created , create_id , create_ip , version , del_flag , info_source ,last_updated ,sort_num
#{id}, #{keyword}, #{msgtype}, #{tousername}, #{fromusername}, #{createtime}, #{content}, #{mediaid}, #{title}, #{description}, #{musicurl}, #{hqmusicurl}, #{thumbmediaid}, #{format}, #{location_x}, #{location_y}, #{scale}, #{label}, #{articlecount}, #{articles_id}, #{picurl}, #{url}, #{default_flag}, #{subscribe_flag} , #{location_precision}, #{event}, #{eventkey}, #{ticket}, #{date_created}, #{create_id}, #{create_ip}, 0, '0', #{info_source}, now(), ${sort_num}

mapper.update:
update tc_wx_info set version=version+1
,last_updated=now()
,update_id=#{update_id}
,update_ip=#{update_ip}
,keyword=#{keyword}
,msgtype=#{msgtype}
,tousername=#{tousername}
,fromusername=#{fromusername}
,createtime=#{createtime}
,content=#{content}
,mediaid=#{mediaid}
,title=#{title}
,description=#{description}
,musicurl=#{musicurl}
,hqmusicurl=#{hqmusicurl}
,thumbmediaid=#{thumbmediaid}
,format=#{format}
,location_x=#{location_x}
,location_y=#{location_y}
,scale=#{scale}
,label=#{label}
,articlecount=#{articlecount}
,articles_id=#{articles_id}
,picurl=#{picurl}
,url=#{url}
,del_flag=#{del_flag}
,info_source=#{info_source}
,location_precision=#{location_precision}  
 ,event=#{event}
,eventkey #{eventkey}
 ,ticket=#{ticket}
,default_flag=#{default_flag}
,subscribe_flag=#{subscribe_flag}
,sort_num=${sort_num}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id ,keyword ,msgtype ,picurl ,url ,musicurl ,hqmusicurl ,articles_id ,title ,content ,description ,default_flag ,subscribe_flag ,last_updated from tc_wx_info where 1=1
and articles_id=#{articles_id}
and del_flag='0' and info_source=#{info_source}
and msgtype=#{msgtype}
and msgtype!='news'
order by last_updated desc,sort_num asc

select t.id, t.keyword, t.msgtype, t.tousername, t.fromusername, t.createtime, t.content, t.title, t.description, t.musicurl, t.hqmusicurl, t.thumbmediaid, t.format, t.location_x, t.location_y, t.scale, t.label, t.picurl, t.url, t.date_created, t.location_precision, t.event, t.eventkey, t.ticket, t2.nickname, t2.remarkname, t2.sex, t2.country, t2.province, t2.city, t2.headimgurl from tc_wx_info t left join tc_wx_user t2 on (t.fromusername=t2.openid) where 1=1
and articles_id=#{articles_id}
and del_flag='0' and info_source=#{info_source}
and msgtype=#{msgtype}
and msgtype!='news'
order by last_updated desc,sort_num asc

mapper.update:
update tc_wx_info 
set version=version+1
,mediaid=#{mediaid}
,thumbmediaid=#{thumbmediaid}
where id=#{id}
